package operator1;

public class Primenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a=5,i;
	for(i=1;i<=a;i++);
	if(a%i==0) {
				System.out.println("Its not a prime number");
		}else {
			System.out.println("Its a prime number");
		}}}
	

	


